  },
  mounted() {
    this.timer = store.state.timer;
    if (this.timer) {
      this.countdown();
    }
  },
  computed: {
    ...mapState({
      timer: state => state.timer,
      pokemonsOwned: state => state.pokemonsOwned,
      coins: state => state.coins
    }),
    timer() {
      return store.state.timer;
    },
    message() {
      return store.state.message;
    }
