    },
    async sellPokemonNotOwned() {
      this.$store.dispatch("removePokemon", this.pokemon.id);
      this.incrementCoins();
      this.hasSelled = true;
    },
    incrementCoins() {
      store.commit("incrementCoins");
    }
  }
