import axios from "axios";
export default {
  name: "Pokedex",
  data() {
    return {
      disabled: false,
      timeout: null,
      pokemonModel: "",
      OpenButtonInfo: 'Abrir',
      counter: "5",
      pokemonsOwned: [{
        id: '1',
        name: 'PokemonEjemplo',
        img: 'xd'
      }],
      pokemon: {
        id: " ",
        name: "",
        img: "https://www.pngitem.com/pimgs/m/580-5807856_pokemon-pokeball-pokeball-transparent-background-hd-png-download.png"
      }
    };
  },
  methods: {
    obtenerPokemon(id) {
      axios.get(`https://pokeapi.co/api/v2/pokemon/${id}`).then(response => {
        console.log(response);
        this.pokemon.img = response.data.sprites.front_default;
        this.pokemon.name = response.data.name;
        this.pokemon.id = response.data.id;
      }).catch(error => {
        this.pokemon.name = "No existe";
      });
    },
    getRandomInt() {
      return Math.floor(Math.random() * 906);
    },
    countDown() {
      if (this.counter >= 0) {
        this.disabled = true;
        this.OpenButtonInfo = this.counter;
        this.timeout = setTimeout(() => {
          this.disabled = false;
          this.counter--;
          this.countDown();
        }, 1000);
      } else {
        this.disabled = false;
        this.counter = 5;
        this.OpenButtonInfo = 'Abrir';
      }
    },
    delay() {
      this.disabled = true;
      this.countDown();
      this.disabled = false;
    },
    guardarPokemon() {
      this.pokemonsOwned.push({
        id: this.pokemon.id,
        name: this.pokemon.name,
        img: this.pokemon.img
      });
      localStorage.setItem('pokemonsOwned', JSON.stringify(this.pokemonOwned));
      console.log(this.pokemonsOwned);
    }
  },
  created: function () {
    let dataLS = JSON.parse(localStorage.getItem('pokemonsOwned'));
    if (dataLS === null) {
      this.pokemonsOwned = [];
    } else {
      this.pokemonsOwned = dataLS;
    }
  }
};
