import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  data: {
    localData: JSON.parse(localStorage.getItem('coins')) || {}
  },
  state: {
  },
  getters: {
  },
  mutations: {
  },
  actions: {
  },
  modules: {
  },
  watch: {
    localData: {
      handler(newValue) {
        localStorage.setItem('coins', JSON.stringify(newValue));
      },
      deep: true
    }
  }
})
